import styled from "styled-components";

export const DataEntryStyle = styled.button`
  align-items: center
  width: "100%";
  display: flex;
  justify-content: space-between;
`;

export const InputStyle = styled.input`
  padding: 0.5em;
  margin: 0.5em;
  color: "palevioletred";
  border: 1px solid black;
  border-radius: 3px;
`;
