import { useState, useEffect } from "react";

import Button from "../Button/Button";
import {
  DataEntryStyle,
  InputStyle,
  ContainerFriends,
  Box,
} from "./DataEntry.style";
import { v4 as uuid } from "uuid";
import "react-responsive-modal/styles.css";
import { Modal } from "react-responsive-modal";
import "./modal-styling.css";

const DataEntry = (props) => {
  const { usersList, setUsersList, onClose, userId } = props;
  const [inputValue, setInputValue] = useState("");
  const [inputFriendValue, setFriendValue] = useState("");

  const [friends, setFriends] = useState([]);
  const [openModal2, setOpenModal2] = useState(false);
  const [friendsSelectable, setFriendsSelectable] = useState([]);
  const userSelected = userId || null;

  // useEffect(() => {
  //   if (userSelected){
  //     setUsersList.
  //   }
  // }, [friends]);

  const checkIfUserExists = (name) => {
    if (
      usersList.filter((e) => e.name.toLowerCase() === name.toLowerCase())
        .length > 0
    ) {
      return true;
    } else return false;
  };

  // const inputFriendChange = (e) => {
  //   if (e) {
  //     if (checkIfUserExists(inputFriendValue)) {
  //       alert(
  //         "Sorry, an user with the same name already exists, choose another name."
  //       );
  //     } else {
  //       const el = { id: uuid(), name: inputFriendValue };
  //       setFrie
  //       setUsersList([...usersList, el]);
  //       setInputValue("");
  //     }
  //   }
  // };

  const addUser = () => {
    try {
      const el = {
        id: uuid(),
        name: inputValue,
        friends: [...friends, setFriendValue],
      };
      if (checkIfUserExists(el.name)) {
        alert(
          "Sorry, an user with the same name already exists, choose another name."
        );
      } else if (inputFriendValue && checkIfUserExists(inputFriendValue)) {
        alert(
          "Sorry, an user with the same name already exists, choose another name for your friend."
        );
      } else {
        setUsersList([...usersList, el]);
        setInputValue("");
        onClose();
      }
    } catch (err) {
      try {
        // retrying to add
        console.log("I'm trying a second time to add a new user");
      } catch (err) {
        alert("Sorry, something went wrong :( ");
      }
    }
  };
  const handleChange = (evt) => {
    setInputValue(evt);
  };
  const handleChangeFriend = (evt) => {
    setFriendValue(evt);
  };

  return (
    <Box>
      <DataEntryStyle>
        <InputStyle
          placeholder="Write..."
          value={inputValue}
          onChange={(e) => handleChange(e.target.value)}
          // onPressEnter={(event) => inputChange(event.target.value)}
        />
        <Button onClick={() => addUser(inputValue)}>{"Save"}</Button>
      </DataEntryStyle>
      <Box>
        Friends:
        <ContainerFriends>
          <Button onClick={() => setOpenModal2(true)}>{"Select Friend"}</Button>

          {"New Friend"}

          <InputStyle
            placeholder="NewFriend"
            value={inputValue}
            onChange={(e) => handleChangeFriend(e.target.value)}
          />
        </ContainerFriends>
        <ul>
          {friends?.map((user, index) => (
            <li>{user.name}</li>
          ))}
        </ul>
      </Box>
      <Modal
        open={openModal2}
        onClose={() => setOpenModal2((prev) => !prev)}
        center
        classNames={{
          overlay: "customOverlay",
          modal: "customModal",
          overlayAnimationIn: "customEnterOverlayAnimation",
          overlayAnimationOut: "customLeaveOverlayAnimation",
          modalAnimationIn: "customEnterModalAnimation",
          modalAnimationOut: "customLeaveModalAnimation",
        }}
      ></Modal>
    </Box>
  );
};

export default DataEntry;
