import React from "react";

import Button from "../Button/Button";
import { DataEntryStyle, InputStyle } from "./DataEntry.style";
const DataEntry = (props) => {
  const { onChangeInput, onPressEnterInput, onClickButton } = props;
  const [inputValue, setInputValue] = useState("");

  const handleChange = (evt) => {
    setInputValue(evt);
  };

  return (
    <DataEntryStyle>
      <InputStyle
        placeholder="Write..."
        value={inputValue}
        onChange={(e) => handleChange(e.target.value)}
        onPressEnter={(event) => onPressEnterInput(event.target.value)}
      />
      <Button onClick={() => onClickButton(inputValue)}>
        <p style={{ fontSize: "20px" }}>{"Save"}</p>
      </Button>
    </DataEntryStyle>
  );
};

export default DataEntry;
