import React, { useEffect, useState } from "react";

import InfiniteScroll from "react-infinite-scroll-component";
import styled from "styled-components";
import { GrLinkDown } from "react-icons/gr";
import { v4 as uuid } from "uuid";

import { deviceQuery } from "./mediaQuery";

//Components
import { Card } from "components";

import "react-responsive-modal/styles.css";
import { Modal } from "react-responsive-modal";
import "./modal-styling.css";

const Container = styled.div`
  background: #e8d6cf;
  padding: 50px 10px;
  text-align: center;

  @media ${deviceQuery.tablet} {
    padding-top: 100px;
  }

  @media ${deviceQuery.laptop} {
    padding-top: 150px;
  }

  @media ${deviceQuery.desktop} {
    padding-top: 160px;
  }
`;

const Title = styled.span`
  text-align: center;
  font-size: 40px;
  font-weight: bold;
  font-family: "Advent Pro", sans-serif;
  background: #e8d6cf;
  width: 100%;
  overflow: hidden;
  position: fixed;
  top: 0;
  display: flex;
  flex-direction: column;
  z-index: 50;

  @media ${deviceQuery.tablet} {
    font-size: 60px;
  }

  @media ${deviceQuery.laptop} {
    font-size: 90px;
  }

  @media ${deviceQuery.desktop} {
    font-size: 100px;
  }
`;

const Sub = styled.span`
    text-align: center;
    font-size: 15px;
    font-weight: bold;
    font-family: "Advent Pro", sans-serif;
    padding-bottom: 5px;
    align-items: center;
    justify-content: center;
    display: flex;
  @media ${deviceQuery.tablet} {
    font-size: 20px;
  }
  @media ${deviceQuery.laptop} {
    font-size: 30px;
  }

  @media ${deviceQuery.desktop} {
    font-size: 40px;
  }
}
  `;

const SubText = styled.span`
    margin: 0 10px;
}
  `;

const ModalText = styled.span`
    font-family: "Advent Pro", sans-serif;
    font-size: 15px;
    @media ${deviceQuery.tablet} {
    font-size: 20px;
  }

  @media ${deviceQuery.laptop} {
    font-size: 40px;
  }

  @media ${deviceQuery.desktop} {
    font-size: 50px;
  }
}
  `;

export const Main = () => {
  const URL = "https://jsonplaceholder.typicode.com/posts";
  const [usersList, setUsersList] = useState([
    { id: uuid(), name: "Mario Rossi" },
    { id: uuid(), name: "Roberto Neri" },
    { id: uuid(), name: "Marina Antonini", friends: ["Mario Rossi"] },
  ]);

  const [page, setPage] = useState(0);
  const [total, setTotal] = useState();
  const [hasMore, setHasMore] = useState(true);
  const [open, setOpen] = useState(false);
  const [content, setContent] = useState("");

  const onOpenModal = () => setOpen(true);
  const onCloseModal = () => setOpen(false);

  // useEffect(() => {
  //   //counting totals element
  //   fetch(URL)
  //     .then((response) => {
  //       if (response.ok) return response.json();
  //       throw new Error("something went wrong while requesting postsList");
  //     })
  //     .then((postsList) => setTotal(postsList?.length))
  //     .catch((error) => console.log("error: ", error));
  // }, []);

  // const moreData = () => {
  //   if (postsList?.length >= total) {
  //     setHasMore(false);
  //     return;
  //   }
  //   setPage(page + 10);
  // };

  // useEffect(() => {
  //   // Async await syntax
  //   const getPosts = async () => {
  //     try {
  //       let url = `${URL}?_start=${page}&_limit=10`;
  //       const response = await fetch(url);
  //       if (!response.ok)
  //         throw new Error("something went wrong while requesting postsList");
  //       const posts = await response.json();
  //       setPostsList([...postsList, ...posts]);
  //     } catch (e) {
  //       console.log(e);
  //     }
  //   };
  //   getPosts();
  //   // eslint-disable-next-line react-hooks/exhaustive-deps
  // }, [page]);

  return (
    <>
      <Title>
        {"Users List"}
        <Sub>
          <GrLinkDown />
          <SubText>{"Here is the list of users you have created"} </SubText>
          <GrLinkDown />
        </Sub>
      </Title>
      {/* <InfiniteScroll
        dataLength={postsList?.length}
        next={() => moreData()}
        hasMore={hasMore}
        loader={postsList?.length === total ? null : <Loading />}
      > */}
      <Container>
        {usersList?.map((user, index) => (
          <Card
            key={index}
            index={index}
            user={user}
            onClickEvent={onOpenModal}
            setContent={setContent}
          />
        ))}
      </Container>
      <Modal
        open={open}
        onClose={onCloseModal}
        center
        classNames={{
          overlay: "customOverlay",
          modal: "customModal",
          overlayAnimationIn: "customEnterOverlayAnimation",
          overlayAnimationOut: "customLeaveOverlayAnimation",
          modalAnimationIn: "customEnterModalAnimation",
          modalAnimationOut: "customLeaveModalAnimation",
        }}
      >
        <ModalText>{content}</ModalText>
      </Modal>
      {/* </InfiniteScroll> */}
    </>
  );
};
