import React, { useContext } from "react";
import styled from "styled-components";
import { ThemeContext } from "../context/ThemeContext";

import Counter from "./Counter";
import { deviceQuery } from "../mediaQuery";

const Container = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  text-align: center;
  max-width: 300px;
  margin: 20px auto;
  padding: 15px 10px;
  background: ${(props) => props.backgroundColor};
  font-family: "Advent Pro", sans-serif;
  font-weight: 600;
  text-transform: uppercase;
  border-radius: 25px;
  min-height: 90px;
  color: #fff;
  cursor: pointer;
  font-size: 15px;

  @media ${deviceQuery.tablet} {
    max-width: 700px;
  }

  @media ${deviceQuery.laptop} {
    max-width: 500px;
    min-height: 20px;
    font-size: 20px;
  }

  @media ${deviceQuery.desktop} {
    max-width: 1200px;
    min-height: 130px;
    font-size: 30px;
  }
`;

const Card = ({ index, user, onClickEvent, setContent }) => {
  const { palette } = useContext(ThemeContext);

  return (
    <Container
      backgroundColor={index % 2 === 0 ? palette[0] : palette[1]}
      onClick={() => {
        onClickEvent();
        setContent(user.name);
      }}
    >
      <Counter number={index + 1} /> {user.name}
    </Container>
  );
};

export default Card;
