import { useState } from "react";

import styled from "styled-components";
import { GrLinkNext } from "react-icons/gr";
import { v4 as uuid } from "uuid";

import { deviceQuery } from "./mediaQuery";
import useModal from "./hooks/useModal";

//Components
import Card from "./components/Card/Card";

import Button from "./components/Button/Button";
import Modal from "./components/Modal/Modal";

import { Title, Sub, SubText, Container } from "./Main.styled";

export const Main = () => {
  const [usersList, setUsersList] = useState([
    { id: uuid(), name: "Mario Rossi" },
    { id: uuid(), name: "Roberto Neri" },
    { id: uuid(), name: "Marina Antonini", friends: ["Mario Rossi"] },
  ]);
  const { isShowing, toggle } = useModal();
  const [open, setOpen] = useState(false);
  const [content, setContent] = useState("");

  const addUser = () => {
    try {
      // add
    } catch (err) {
      try {
        // retrying to add
        console.log("I'm trying a second time to add a new user");
      } catch (err) {
        alert("Sorry, something went wrong :( ");
      }
    }
  };

  return (
    <>
      <Title>
        {"Users List"}
        <Sub>
          <SubText>{"View the list of users created or "} </SubText>
          <GrLinkNext />
          <Button onClick={toggle}>Add user</Button>
        </Sub>
      </Title>

      <Container>
        {usersList?.map((user, index) => (
          <Card
            key={index}
            index={index}
            user={user}
            onClickEvent={() => setOpen((prev) => !prev)}
            setContent={setContent}
          />
        ))}
      </Container>

      <Modal visible={isShowing}>
        <h1>Surprise!</h1>
        <Button onClick={toggle}>Close</Button>
      </Modal>
      {/* <Modal
        open={open}
        onClose={() => setOpen((prev) => !prev)}
        center
        classNames={{
          overlay: "customOverlay",
          modal: "customModal",
          overlayAnimationIn: "customEnterOverlayAnimation",
          overlayAnimationOut: "customLeaveOverlayAnimation",
          modalAnimationIn: "customEnterModalAnimation",
          modalAnimationOut: "customLeaveModalAnimation",
        }}
      >
        <ModalText>{content}</ModalText>
      </Modal> */}
    </>
  );
};
