import { useState } from "react";

import Button from "../Button/Button";
import { DataEntryStyle, InputStyle } from "./DataEntry.style";
import { v4 as uuid } from "uuid";

const DataEntry = (props) => {
  const { usersList, setUsersList } = props;
  const [inputValue, setInputValue] = useState("");

  const inputChange = (e) => {
    if (e) {
      const el = { id: uuid(), name: inputValue };
      setUsersList([...usersList, el]);
      setInputValue("");
    }
  };

  const addUser = (e) => {
    try {
      console.log("e", e);
    } catch (err) {
      try {
        // retrying to add
        console.log("I'm trying a second time to add a new user");
      } catch (err) {
        alert("Sorry, something went wrong :( ");
      }
    }
  };
  const handleChange = (evt) => {
    setInputValue(evt);
  };

  return (
    <DataEntryStyle>
      <InputStyle
        placeholder="Write..."
        value={inputValue}
        onChange={(e) => handleChange(e.target.value)}
        onPressEnter={(event) => inputChange(event.target.value)}
      />
      <Button onClick={() => addUser(inputValue)}>
        <p style={{ fontSize: "20px" }}>{"Save"}</p>
      </Button>
    </DataEntryStyle>
  );
};

export default DataEntry;
