import styled from "styled-components";

export const DataEntryStyle = styled.button`
  margin: 20px 0;
  width: 70%;
  display: flex;
  flex-direction: row;
`;

const Input = styled.input`
  padding: 0.5em;
  margin: 0.5em;
  color: "palevioletred";
  border: 1px solid black;
  border-radius: 3px;
`;
